import React, { useState } from 'react';
import SearchBar from '../../components/SearchBar/SearchBar';
import ChampionCard from '../../components/ChampionCard/ChampionCard';
import { fetchChampionData } from '../../services/api';

const HomePage = () => {
  const [champion, setChampion] = useState(null);

  const handleSearch = async (championName) => {
    const data = await fetchChampionData(championName);
    setChampion(data);
  };

  return (
    <div>
      {/* Ajuste o caminho da imagem para o correto */}
      <img src={process.env.PUBLIC_URL + '/assets/imagens/LoL_Logo_nome_colorido.png'} alt="Logo do League of Legends" />
      <SearchBar onSearch={handleSearch} />
      <ChampionCard champion={champion} />
    </div>
  );
};

export default HomePage;
