import React, { useEffect, useState } from 'react';
import './championCard.css';

const ChampionCard = ({ champion }) => {
  const [championDetails, setChampionDetails] = useState(null);

  useEffect(() => {
    const fetchChampionDetails = async () => {
      if (champion) {
        const response = await fetch(`https://ddragon.leagueoflegends.com/cdn/14.12.1/data/en_US/champion/${champion.id}.json`);
        const data = await response.json();
        setChampionDetails(data.data[champion.id]);
      }
    };

    fetchChampionDetails();
  }, [champion]);

  if (!championDetails) return null;

  return (
    <div className="championCard">
      <div className="championInfo">
        <img className="championImage" src={`http://ddragon.leagueoflegends.com/cdn/img/champion/splash/${champion.id}_0.jpg`} alt={champion.name} />
        <div className="championDetails">
          <h2 className="championName">{championDetails.name}</h2>
          <p className="championTitle">{championDetails.title}</p>
          <p className="championBlurb">{championDetails.blurb}</p>
        </div>
      </div>
      {championDetails.spells ? (
        <div className="championAbilities">
          {championDetails.spells.map((spell, index) => (
            <div key={index} className="championAbility">
              <img className="abilityImage" src={`http://ddragon.leagueoflegends.com/cdn/14.12.1/img/spell/${spell.image.full}`} alt={spell.name} />
              <div className="abilityDetails">
                <p className="abilityName">{spell.name}</p>
                <p className="abilityDescription">{spell.description}</p>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No abilities found.</p>
      )}
    </div>
  );
};

export default ChampionCard;
